  font-family: sans-serif;
  font-size: 100%;
  line-height: 1.15;
  min-width: 5vw;
  height: 5vh;
  width:15vh;

  overflow: visible;
  border-radius: 6px;
  margin:auto;
  margin-top: 1em;
  margin-bottom: 1em;
  background-color: #f4753c;
  color: white;

  border: none;

  cursor: ${(props) => (props.disabled ? "not-allowed" : "pointer")};
