export const Button = styled.button`
box-sizing: inherit;
font-family: sans-serif;
font-size: 100%;
line-height: 1.15;
min-width: 5vw;
height: min-content;
width:15vh;

border-radius: 6px;
margin:auto;
margin-top: .5em;
margin-bottom: 1em;
margin-left:.5%;
margin-right:.5%;
background-color: #f4753c;
color: white;
  border: none;
  cursor: ${(props) => (props.disabled ? "not-allowed" : "pointer")};
  &:hover {
    background-color: ${(props) => (props.disabled ? "gray" : "#c85823")};
    color: black;
    background-color:  #ffff;
    border: 2px solid #f4753c;
  }
`;

export const RemoveButtons= (props) => {    

const RemoveAll =()=>{
  props.action(null,"RemoveAll")
}

const removeEditor=()=>{
    props.action(null,"RemoveEditor")
  }

   return (
       <>
               <Button onClick={removeEditor}>Delete</Button>
               <Button onClick={RemoveAll}>Delete All</Button>
       </>
   );
}
