import React, { Component } from "react";
import styled from "styled-components";

export const Button = styled.button`
box-sizing: inherit;
font-family: sans-serif;
font-size: 100%;
line-height: 1.15;
min-width: 5vw;



border-radius: 6px;
margin:auto;
margin-top: .5em;
margin-bottom: 1em;
margin-left:.5%;
margin-right:.5%;
background-color: #f4753c;
color: white;
border: none;
cursor: ${(props) => (props.disabled ? "not-allowed" : "pointer")};
&:hover {
  background-color: ${(props) => (props.disabled ? "gray" : "#c85823")};
  color: black;
  background-color:  #ffff;
  border: 2px solid #f4753c;
}
`;


export const UnUpLineWidthWrapper = styled.div`
display: flex;
padding: 0%;
align-items: center;
flex-direction: row;
// justify-content: space-around;
`;

  export const UnUpLineWidthSpin=(props)=> {


  const add = () => {
    props.action("","+UnUpLineWidth");
}
  const subtract= () => {
    props.action("","-UnUpLineWidth");
}
        return (
            <>


                  <UnUpLineWidthWrapper>
                  Line Width:
                    <input
                        style={{maxWidth: '7%', textAlign:'center', padding:'0%;'}}
                    type="numeric"
                    value={props.num}
                    />
                    </UnUpLineWidthWrapper>
                    <Button onClick={subtract}>-</Button>
                    <Button onClick={add}>+</Button>

            </>
        );
}
