import React, { Component } from "react";
import styled from "styled-components";
import { UnUpLineColorPicker } from "../ColorPickers/UnUpLineColorPicker.js"

export const Button = styled.button`
box-sizing: inherit;
font-family: sans-serif;
font-size: 100%;
line-height: 1.15;
min-width: 5vw;



border-radius: 6px;
margin:auto;
margin-top: .5em;
margin-bottom: 1em;
margin-left:1.2%;
margin-right:1%;
background-color: #f4753c;

color: white;
border: none;
cursor: ${(props) => (props.disabled ? "not-allowed" : "pointer")};
&:hover {
  background-color: ${(props) => (props.disabled ? "gray" : "#c85823")};
  color: black;
  background-color:  #ffff;
  border: 2px solid #f4753c;
}
`;


export const UnUpLineWidthWrapper = styled.div`
display: flex;
padding: 0%;
padding-top:2%;
padding-left:2%;
align-items: center;
flex-direction: row;
// justify-content: space-around;
`;


export const UnUpLineColorWrapper = styled.div`
display: flex;
padding: 3%;
flex-direction: column;
`;



  export const UnUpLineWidthSpin=(props)=> {


  const add = () => {
    props.action("","+UnUpLineWidth");
}
  const subtract= () => {
    props.action("","-UnUpLineWidth");
}
        return (
            <>


                  <UnUpLineWidthWrapper>
                  Line Width:
                    <input
                        style={{maxWidth: '7%', textAlign:'center', padding:'0%;'}}
                    type="numeric"
                    value={props.num}
                    />
                    <span style={{width:'20%'}}></span>
                    <label > Line Color:</label>
                    <span style={{width:'5%'}}></span>
                    <img className="NodeIcons" src={null} style={{backgroundColor:props.color,width:'10px;', height:"20px",marginLeft:"2%;"}} alt=""/>

                    </UnUpLineWidthWrapper>
                    <UnUpLineWidthWrapper>
                    <Button onClick={subtract}>-</Button>
                    <Button onClick={add}>+</Button>
                    <span style={{width:'20%'}}></span>
                    <Button>Select</Button>
                    </UnUpLineWidthWrapper>


            </>
        );
}
