import React from "react";
import styled from "styled-components";

export const Button = styled.button`
box-sizing: inherit;
font-family: sans-serif;
font-size: 100%;
line-height: 1.15;
min-width: 5vw;
height: min-content;
width:min-content;


border-radius: 6px;
margin:auto;
margin-top: .5em;
margin-bottom: 1em;
margin-left:.5%;
margin-right:.5%;
background-color: #f4753c;
color: white;
  border: none;
  cursor: ${(props) => (props.disabled ? "not-allowed" : "pointer")};
  &:hover {
    background-color: ${(props) => (props.disabled ? "gray" : "#c85823")};
    color: black;
    background-color:  #ffff;
    border: 2px solid #f4753c;
  }
`;


  export const EditorButtons= (props) => {


    const Clear= () => {
        console.log("clear")
        props.action(null,"Clear")
    }

    const Add=()=>{
        props.action3('add')
    }

    const ButtonFunction = (e) => {
      if(e === "Up"){
              props.action(null,'MoveUp')}
      else if(e === "Down"){
              props.action(null,'MoveDown')}
        }      



      return (

        <div style={{display: 'flex', flexDirection: 'row' ,marginLeft:'0',justifyContent:'center'}} >

        <span style={{width:'15%'}}></span>
          <Button onClick={props.action3}>Edit</Button>
          <Button onClick={Add}>Add</Button>
          <Button onClick={Clear}>Clear</Button>
        </div> 


      );
    }     

