import React, { Component } from "react";
import styled from "styled-components";
import { UnUpLineColorPicker } from "../ColorPickers/UnUpLineColorPicker.js"

export const Button = styled.button`
box-sizing: inherit;
font-family: sans-serif;
font-size: 100%;
line-height: 1.15;
min-width: 5vw;



border-radius: 6px;
margin:auto;
margin-top: .5em;
margin-bottom: 1em;
margin-left:1.2%;
margin-right:1%;
background-color: #f4753c;

color: white;
border: none;
cursor: ${(props) => (props.disabled ? "not-allowed" : "pointer")};
// &:hover {
//   background-color: ${(props) => (props.disabled ? "gray" : "#c85823")};
//   color: black;
//   background-color:  #ffff;
//   border: 2px solid #f4753c;
// }
`;





  export const UnUpLineWidthSpin=(props)=> {


  const add = () => {
    props.action("","+UnUpLineWidth");
}
  const subtract= () => {
    props.action("","-UnUpLineWidth");
}
        return (
            <>      
              <div style={{display: 'flex', flexDirection: 'row'}} >

                    <h4 style={{marginTop:'14px',marginBottom:'5px'}}> Line Width:</h4>
                    <input
                        style={{height: '28px',maxWidth: '7%', textAlign:'center', justifyContent:'center', padding:'0%;',marginTop:'11px'}}
                    type="numeric"
                    value={props.num}
                    />
                    <Button onClick={subtract} style={{marginTop:"12px",marginBottom:"15px"}}>-</Button>
                    <Button onClick={add} style={{marginTop:"12px",marginBottom:"15px"}}>+</Button>
                    <span style={{width:'5%'}}></span>
                    <h4 style={{marginTop:"12px",marginBottom:"15px"}}> Line Color:</h4>
                    <span style={{width:'2.3%'}}></span>
                    <img className="NodeIcons" src={null} style={{backgroundColor:props.color,width:'10px;', height:"20px",marginLeft:"2%;",marginTop:"14px",marginBottom:"15px"}} alt=""/>
                    <Button style={{marginLeft:"2%;",marginTop:"12px",marginBottom:"15px"}}>Select</Button>
              </div>




            </>
        );
}
